import React, { Component } from 'react';

// Mock CommentService to resolve the error.  In a real application, this file would
// contain functions for making API requests related to comments.  For this example,
// we'll just create a simple object with the methods that are used in this component.
const CommentService = {
    getCommentById: (postId, commentId) => {
        // Simulate an API call that returns a promise.
        return new Promise((resolve, reject) => {
            // Simulate a successful response after a short delay.
            setTimeout(() => {
                if (postId && commentId) {
                    resolve({
                        data: {
                            id: commentId,
                            comment: `This is a comment with ID ${commentId} for post ${postId}`,
                            userId: 'Test User' //  Use 'userId' to match the component's state
                        }
                    });
                } else {
                    reject({ message: "Post ID or Comment ID is missing" }); //reject
                }

            }, 500); // Simulate a 500ms delay
        });
    },
    updateComment: (postId, updatedComment, commentId) => {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                if (postId && updatedComment && commentId) {
                  console.log(`Comment ${commentId} for post ${postId} updated with:`, updatedComment);
                  resolve({ data: updatedComment }); // Simulate success
                }
                else{
                    reject({ message: "Post ID, Comment ID or updated comment is missing."});
                }

            }, 500);
        });
    }
};



class UpdateCommentComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: this.props.match.params.commentId,
            postId: this.props.match.params.id,
            comment: '',
            name: '',
            loading: false,
            error: null
        }
        this.changeCommentHandler = this.changeCommentHandler.bind(this);
        this.changeNameHandler = this.changeNameHandler.bind(this);
        this.updateComment = this.updateComment.bind(this);
        this.cancel = this.cancel.bind(this);
    }

    componentDidMount() {
        this.setState({ loading: true, error: null });
        CommentService.getCommentById(this.state.postId, this.state.id)
            .then(res => {
                let comment = res.data;
                this.setState({
                    comment: comment.comment,
                    name: comment.userId,
                    loading: false
                });
            })
            .catch(error => {
                console.error("Error fetching comment:", error);
                this.setState({ loading: false, error: "Failed to load comment details." });
            });
    }

    updateComment = (e) => {
        e.preventDefault();
        let updatedComment = {
            comment: this.state.comment,
            userId: this.state.name
        };
        console.log('updatedComment => ' + JSON.stringify(updatedComment));

        CommentService.updateComment(this.state.postId, updatedComment, this.state.id)
            .then(res => {
                this.props.history.push(`/posts`);
            })
            .catch(error => {
                console.error("Error updating comment:", error);
                this.setState({ error: "Failed to update comment." });
            });
    }

    changeCommentHandler = (event) => {
        this.setState({ comment: event.target.value });
    }

    changeNameHandler = (event) => {
        this.setState({ name: event.target.value });
    }

    cancel() {
        this.props.history.push(`/posts`);
    }

    render() {
        if (this.state.loading) {
            return <div>Loading comment details...</div>;
        }

        if (this.state.error) {
            return <div className="alert alert-danger">{this.state.error}</div>;
        }

        return (
            <div>
                <br />
                <div className="container">
                    <div className="row">
                        <div className="card col-md-6 offset-md-3 offset-md-3">
                            <h3 className="text-center">Update Comment</h3>
                            <div className="card-body">
                                <form>
                                    <div className="form-group">
                                        <label> Comment: </label>
                                        <input placeholder="Comment" name="comment" className="form-control"
                                            value={this.state.comment} onChange={this.changeCommentHandler} />
                                    </div>
                                    <div className="form-group">
                                        <label> Name: </label>
                                        <input placeholder="Your Name" name="name" className="form-control"
                                            value={this.state.name} onChange={this.changeNameHandler} />
                                    </div>

                                    <button className="btn btn-success" onClick={this.updateComment}>Save</button>
                                    <button className="btn btn-danger" onClick={this.cancel}>Cancel</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default UpdateCommentComponent;
