import React, { Component } from 'react';
import PostService from '../services/PostService';
import { withRouter } from 'react-router-dom'; // Import withRouter

class CreatePostComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      content: ''
    };
    this.changeTitleHandler = this.changeTitleHandler.bind(this);
    this.changeContentHandler = this.changeContentHandler.bind(this);
    this.savePost = this.savePost.bind(this);
    this.cancel = this.cancel.bind(this);
  }

  changeTitleHandler(event) {
    this.setState({ title: event.target.value });
  }

  changeContentHandler(event) {
    this.setState({ content: event.target.value });
  }

  savePost = (e) => {
    e.preventDefault();
    let post = { title: this.state.title, content: this.state.content };
    console.log('post => ' + JSON.stringify(post));

    PostService.createPost(this.state.title, this.state.content)
      .then(res => {
        this.props.history.push('/posts'); // Use history.push for navigation
      });
  }

  cancel() {
    this.props.history.push('/posts'); // Use history.push for navigation
  }

  render() {
    return (
      <div>
        <h2>Add New Post</h2>
        <form>
          <div>
            <label>Title:</label>
            <input type="text" value={this.state.title} onChange={this.changeTitleHandler} />
          </div>
          <div>
            <label>Content:</label>
            <textarea value={this.state.content} onChange={this.changeContentHandler} />
          </div>
          <button onClick={this.savePost}>Save</button>
          <button onClick={this.cancel}>Cancel</button>
        </form>
      </div>
    );
  }
}

export default withRouter(CreatePostComponent); // Wrap with withRouter
