import React, { Component } from 'react';
import CommentService from '../services/CommentService';

class UpdateCommentComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: this.props.match.params.commentId,
            postId: this.props.match.params.id,
            comment: '',
            name: '', // Changed from author
            loading: false,
            error: null
        }
        this.changeCommentHandler = this.changeCommentHandler.bind(this);
        this.changeNameHandler = this.changeNameHandler.bind(this); // Changed from changeAuthorHandler
        this.updateComment = this.updateComment.bind(this);
        this.cancel = this.cancel.bind(this);
    }

    componentDidMount() {
        this.setState({ loading: true, error: null });
        CommentService.getCommentById(this.state.postId, this.state.id)
            .then(res => {
                let comment = res.data;
                this.setState({
                    comment: comment.comment,
                    name: comment.userId, // Assuming userId in your backend corresponds to the author/name
                    loading: false
                });
            })
            .catch(error => {
                console.error("Error fetching comment:", error);
                this.setState({ loading: false, error: "Failed to load comment details." });
            });
    }

    updateComment = (e) => {
        e.preventDefault();
        let updatedComment = {
            comment: this.state.comment,
            userId: this.state.name // Using name for userId in the update
        };
        console.log('updatedComment => ' + JSON.stringify(updatedComment));

        CommentService.updateComment(this.state.postId, updatedComment, this.state.id)
            .then(res => {
                this.props.history.push(`/posts`); // Redirect back to the posts list
            })
            .catch(error => {
                console.error("Error updating comment:", error);
                this.setState({ error: "Failed to update comment." });
            });
    }

    changeCommentHandler = (event) => {
        this.setState({ comment: event.target.value });
    }

    changeNameHandler = (event) => {
        this.setState({ name: event.target.value });
    }

    cancel() {
        this.props.history.push(`/posts`);
    }

    render() {
        if (this.state.loading) {
            return <div>Loading comment details...</div>;
        }

        if (this.state.error) {
            return <div className="alert alert-danger">{this.state.error}</div>;
        }

        return (
            <div>
                <br />
                <div className="container">
                    <div className="row">
                        <div className="card col-md-6 offset-md-3 offset-md-3">
                            <h3 className="text-center">Update Comment</h3>
                            <div className="card-body">
                                <form>
                                    <div className="form-group">
                                        <label> Comment: </label>
                                        <input placeholder="Comment" name="comment" className="form-control"
                                               value={this.state.comment} onChange={this.changeCommentHandler} />
                                    </div>
                                    <div className="form-group">
                                        <label> Name: </label>
                                        <input placeholder="Your Name" name="name" className="form-control"
                                               value={this.state.name} onChange={this.changeNameHandler} />
                                    </div>

                                    <button className="btn btn-success" onClick={this.updateComment}>Save</button>
                                    <button className="btn btn-danger" onClick={this.cancel}>Cancel</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default UpdateCommentComponent;
